  CREATE OR REPLACE TRIGGER "RIPPLE"."HHH" 
   before insert on "RIPPLE"."ACCIDENTS" 
   for each row 
begin  
   if inserting then 
      if :NEW."ACCIDENT_ID" is null then 
         select ACCIDENT_ID_SEQ.nextval into :NEW."ACCIDENT_ID" from dual; 
      end if; 
   end if; 
end;


/
